export async function countRows(
  tableName: string,
  whereClause?: string,
  params?: unknown[],
) {
  const sql = whereClause
    ? `SELECT COUNT(*) as count FROM ${tableName} WHERE ${whereClause}`
    : `SELECT COUNT(*) as count FROM ${tableName}`;

  const rows = await dbQuery(sql, params || []);
  return rows[0]?.count || 0;
}

